import React, { Component } from 'react';
import RestaurantCard from "./restaurantCard";
class RestaurantsContainer extends Component {
  constructor () {
    this.state = {
      restaurants: []
    }
  }

  
  
  async componentDidMount () {
    const restaurantsFromDb = await axios.get('db_url');
    // Make sure you're getting an array back here
    console.log(restaurantsFromDb)
    if (restaurantsFromDb) {
      return this.setState({
        restaurants: restaurantsFromDb
      })
    }
    console.log('No restaurants found from initial DB fetch')
  }
  
  render() {
   return (
     <>
    {this.state.restaurants.map = restaurant => (
      <RestaurantCard 
        key={restaurant.id}
        name={restaurant.name} 
        photo={restaurant.photo}
        rating={restaurant.rating}
      />
    )} 
  </>
   )}
}

export default RestaurantsContainer;
